import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "ui",
  initialState: { amount: 0 },
  reducers: {
    increment(state) {
      state.amount++;
    },
    decrement(state) {
      state.amount--;
    },
  },
});

//exported actions of the ui slice
export const cartActions = cartSlice.actions;
//exported slice
export default cartSlice;
