import { useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import { useSelector, useDispatch } from "react-redux";
import { uiActions } from "./store/ui-slice";

function App() {
  const dispatch = useDispatch();

  const toggleUi = useSelector((state) => state.ui.cartIsVisible);
  //entire cart from cart-slice store.
  const cart = useSelector((state) => state.cart);

  //useEffect to check for changes in the dependencies OF THE CART

  useEffect(() => {
    const sendCartData = async () => {
      //first status for the notification we pass an object.since redux receives and object
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending Cart Data...",
        })
      );
      const response = await fetch(
        "https://reduxcart-project-6b2ff-default-rtdb.firebaseio.com/cart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );
      //error handling
      if (!response.ok) {
        throw new Error("sending cart data failed");
      }

      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Sucess sending cart data...",
          message: "Sending Cart Data...",
        })
      );

      const responseData = await response.json();
    };

    sendCartData().catch(error => {
      uiActions.showNotification({ 
        status:"Error", 
      title: 'Error while sending data...',
      message: "Error, not possible to send data..."
    })
    })
  }, [cart, ]);
  return (
    <Layout>
      {toggleUi && <Cart />}
      <Products />
    </Layout>
  );
}

export default App;
