import { createSlice } from "@reduxjs/toolkit";

const cartSlice = createSlice({
  name: "cart",
  initialState: {items: []},
  reducers: {
    increment(state) {
      state.amount++;
    },
    decrement(state) {
    },
  },
});

//exported actions of the ui slice
export const cartActions = cartSlice.actions;
//exported slice
export default cartSlice;
