import { Fragment, useEffect } from "react";
import Cart from "./components/Cart/Cart";
import Layout from "./components/Layout/Layout";
import Products from "./components/Shop/Products";
import Notification from "./components/UI/Notification";
import { useSelector, useDispatch } from "react-redux";
import { uiActions } from "./store/ui-slice";

let isInitial = true //variable to check at the initilization of the app useEffect does not run

function App() {
  const dispatch = useDispatch();

  const toggleUi = useSelector((state) => state.ui.cartIsVisible);

  const cart = useSelector((state) => state.cart); //select and retrieve the entire cart from cart-slice store.
  const notification = useSelector((state) => state.ui.notification); //retrieving notification object to display properties
  //useEffect to check for changes in the dependencies OF THE CART
  useEffect(() => {
    const sendCartData = async () => {
      //first status for the notification we pass an object.since redux receives and object
      dispatch(
        uiActions.showNotification({
          status: "pending",
          title: "Sending...",
          message: "Sending Cart Data...",
        })
      );
      const response = await fetch(
        "https://reduxcart-project-6b2ff-default-rtdb.firebaseio.com/cart.json",
        { method: "PUT", body: JSON.stringify(cart) }
      );
      //error handling
      if (!response.ok) {
        throw new Error("sending cart data failed");
      }

      dispatch(
        uiActions.showNotification({
          status: "success",
          title: "Sucess sending cart data...",
          message: "Sending Cart Data...",
        })
      );

      // const responseData = await response.json();
    };

    if(isInitial){
      isInitialreturn }

    sendCartData().catch((error) => {
      uiActions.showNotification({
        status: "Error",
        title: "Error while sending data...",
        message: "Error, not possible to send data...",
      });
    });
  }, [cart, dispatch]);
  return (
    <Fragment>
      {notification && (
        <Notification
          status={notification.status}
          title={notification.title}
          message={notification.message}
        />
      )}
      <Layout>
        {toggleUi && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
